angular.module('wazuhApp',[
  'ui.router',
  'ngMaterial',
  'ngLoadScript'
]);


(function () {
  function routes( $stateProvider, $locationProvider, $mdIconProvider, $mdThemingProvider) {
    $mdThemingProvider.theme('default').primaryPalette('blue').accentPalette('blue');
    $locationProvider.html5Mode(true);
    //console.log("loading path...",$location.absUrl());

    $stateProvider
    .state("overview", {
        templateUrl : "http://192.168.0.157:8000/en-US/static/app/wazuh/views/overview.html",
        controller : 'overviewCtrl',
        controllerAs: 'oc'
    })
    .state("manager", {
        templateUrl : "http://192.168.0.157:8000/en-US/static/app/wazuh/views/manager.html",
        controller : 'managerCtrl',
        controllerAs: 'mc'
    })
    .state("agents", {
        templateUrl : "http://192.168.0.157:8000/en-US/static/app/wazuh/views/test2.html",
        controller : 'agentCtrl',
        controllerAs: 'ac'
    });

  }

  angular.module('wazuhApp')
  .config([ '$stateProvider','$locationProvider', '$mdIconProvider', '$mdThemingProvider', routes]);

})();

(function (ng) {
    'use strict';
    var app = ng.module('ngLoadScript', []);
  
    app.directive('script', function() {
      return {
        restrict: 'E',
        scope: false,
        link: function(scope, elem, attr) {
          if (attr.type === 'text/javascript-lazy') {
            var code = elem.text();
            var f = new Function(code);
            f();
          }
        }
      };
    });
  
  }(angular));
